import React, { useState } from "react";
import FilmSlider from "./FilmSlider";

interface UserInfoProps {
  open: boolean;
}

const UserInfo: React.FC<UserInfoProps> = ({ open }) => {
  const [Confirmed, isConfirmed] = useState(open);
  const confirm = React.useCallback(() => {
    isConfirmed(true);
  }, [isConfirmed]);
  const back = React.useCallback(() => {}, [isConfirmed]);
  return (
    <>
      <div className="UserInfo">
        <text className="title"> Imię Nazwisko</text>
        <text className="content">Witaj uytkowniku xyz.</text>
        <div className="row">
          <button>
            <text onClick={confirm}>Przejdź</text>
          </button>
          <button>
            <text onClick={back}>wróć</text>
          </button>
        </div>
      </div>

      {Confirmed && (
        <>
          <div className="movies">
            <FilmSlider />
          </div>
        </>
      )}
    </>
  );
};
export default UserInfo;
